import React, { useState } from 'react';
import './DroneStatusDashboard.css';

// Dados Falsos (serão substituídos pela API na próxima tarefa)
const mockDrones = [
    { id: 1, status: 'Idle', bateria: 100 },
    { id: 2, status: 'Em voo', bateria: 65 },
    { id: 3, status: 'Retornando', bateria: 25 },
    { id: 4, status: 'Carregando', bateria: 80 },
];

function DroneStatusDashboard() {
    const [drones, setDrones] = useState(mockDrones);

    // Função auxiliar para obter a cor do status
    const getStatusColor = (status) => {
        switch (status) {
            case 'Idle': return 'green';
            case 'Em voo': return 'blue';
            case 'Retornando': return 'orange';
            case 'Carregando': return 'purple';
            default: return 'gray';
        }
    };

    return (
        <div className="dashboard-container">

        </div>
    );
}

export default DroneStatusDashboard;
