import React, { useState } from 'react';
import './PedidoForm.css';

function PedidoForm() {
    // Estados para cada campo do formulário
    const [localizacaoX, setLocalizacaoX] = useState('');
    const [localizacaoY, setLocalizacaoY] = useState('');
    const [peso, setPeso] = useState('');
    const [prioridade, setPrioridade] = useState('baixa');

    // Função chamada ao submeter o formulário
    const handleSubmit = (event) => {
        event.preventDefault(); // Impede o recarregamento da página

        // Por enquanto, apenas exibimos os dados no console
        // Na próxima tarefa, faremos a chamada para a API aqui
        console.log('Novo Pedido:', {
            localizacao_x: localizacaoX,
            localizacao_y: localizacaoY,
            peso_kg: peso,
            prioridade: prioridade,
        });

        // Limpa o formulário após o envio
        setLocalizacaoX('');
        setLocalizacaoY('');
        setPeso('');
        setPrioridade('baixa');
    };

    return (
        <div className="pedido-form-container">
            <h2>Criar Novo Pedido</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Localização X:</label>
                    <input 
                        type="number" 
                        value={localizacaoX} 
                        onChange={(e) => setLocalizacaoX(e.target.value)}
                        required 
                    />
                </div>
                <div className="form-group">
                    <label>Localização Y:</label>
                    <input 
                        type="number" 
                        value={localizacaoY} 
                        onChange={(e) => setLocalizacaoY(e.target.value)}
                        required 
                    />
                </div>
                <div className="form-group">
                    <label>Peso (kg):</label>
                    <input 
                        type="number"
                        step="0.1"
                        value={peso} 
                        onChange={(e) => setPeso(e.target.value)}
                        required 
                    />
                </div>
                <div className="form-group">
                    <label>Prioridade:</label>
                    <select value={prioridade} onChange={(e) => setPrioridade(e.target.value)}>
                        <option value="baixa">Baixa</option>
                        <option value="media">Média</option>
                        <option value="alta">Alta</option>
                    </select>
                </div>
                <button type="submit">Enviar Pedido</button>
            </form>
        </div>
    );
}

export default PedidoForm;
