// src/App.js

import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import PedidoForm from './components/PedidoForm';
import DroneStatusDashboard from './components/DroneStatusDashboard';
import DeliveryQueue from './components/DeliveryQueue';
import api from './services/api';

function App() {
  // O estado da fila de pedidos agora vive no componente pai (App)
  const [queue, setQueue] = useState([]);

  // Função para buscar os pedidos, envolta em useCallback para otimização
  const fetchPedidos = useCallback(async () => {
    try {
      const response = await api.get('/pedidos');
      setQueue(response.data);
    } catch (error) {
      console.error("Erro ao buscar a fila de pedidos:", error);
    }
  }, []);

  // useEffect para buscar os pedidos quando o componente montar
  useEffect(() => {
    fetchPedidos();
  }, [fetchPedidos]);


  return (
    <div className="App">
      <header className="App-header">
        <h1>Dashboard de Logística de Drones</h1>
      </header>
      <main className="main-container">
        <div className="left-panel">
          {/* Passamos a função fetchPedidos para o formulário */}
          <PedidoForm onPedidoCriado={fetchPedidos} />
          {/* Passamos a lista de pedidos para a fila */}
          <DeliveryQueue queue={queue} />
        </div>
        <div className="right-panel">
          <DroneStatusDashboard />
        </div>
      </main>
    </div>
  );
}

export default App;
