import React, { useState } from 'react';
import './PedidoForm.css';

function PedidoForm() {
    // Estados para cada campo do formulário
    const [localizacaoX, setLocalizacaoX] = useState('');
    const [localizacaoY, setLocalizacaoY] = useState('');
    const [peso, setPeso] = useState('');
    const [prioridade, setPrioridade] = useState('baixa');

    // Função chamada ao submeter o formulário
    const handleSubmit = (event) => {
        event.preventDefault(); // Impede o recarregamento da página

        // Por enquanto, apenas exibimos os dados no console
        // Na próxima tarefa, faremos a chamada para a API aqui
        console.log('Novo Pedido:', {
            localizacao_x: localizacaoX,
            localizacao_y: localizacaoY,
            peso_kg: peso,
            prioridade: prioridade,
        });

        // Limpa o formulário após o envio
        setLocalizacaoX('');
        setLocalizacaoY('');
        setPeso('');
        setPrioridade('baixa');
    };

    return (
        <div className="pedido-form-container">

        </div>
    );
}

export default PedidoForm;
